mapper.insert:
INSERT INTO SHBOARD (ID, TITLE, CONTENT, WDATE, REF, STEP, DEPTH, PARENT, DEL, HIDDEN, READCOUNT, FILENAME, ENT) VALUES (#{id}, #{title}, #{content}, DATE_FORMAT(NOW(), '%Y-%m-%d'), (SELECT IFNULL(MAX(REF), 0) + 1 FROM SHBOARD b) ,0,0,0,0,#{hidden},0,
#{filename}
null ,
#{ent}
null
)

INSERT INTO SHBOARD (ID, TITLE, CONTENT, WDATE, REF, STEP, DEPTH, PARENT, DEL, HIDDEN, READCOUNT, FILENAME, ENT) VALUES ( #{id}, #{title}, #{content}, DATE_FORMAT(NOW(), '%Y-%m-%d'), ( SELECT * FROM ( SELECT REF FROM SHBOARD WHERE SEQ=#{seq})a), ( SELECT * FROM ( SELECT STEP+1 STEP FROM SHBOARD WHERE SEQ=#{seq})a), ( SELECT * FROM ( SELECT DEPTH+1 DEPTH FROM SHBOARD WHERE SEQ=#{seq})a), #{seq}, 0, ( SELECT * FROM ( SELECT HIDDEN FROM SHBOARD WHERE SEQ=#{seq})a), 0,
#{filename}
null ,
#{ent}
null
)A

mapper.update:
UPDATE SHBOARD SET
TITLE=#{title}, CONTENT=#{content},WDATE=DATE_FORMAT(NOW(), '%Y-%m-%d'),FILENAME=#{filename}
WHERE SEQ=#{seq}

mapper.select:
SELECT rnn,SEQ ,ID ,TITLE,CONTENT, WDATE,REF,STEP,DEPTH,PARENT,DEL,HIDDEN,READCOUNT,FILENAME,ENT,TYPE FROM ( SELECT @ROWNUM := @ROWNUM + 1 as rnn, SEQ ,ID ,TITLE,CONTENT, WDATE,REF,STEP,DEPTH,PARENT,DEL,HIDDEN,READCOUNT,FILENAME,ENT,TYPE FROM SHBOARD,(SELECT @ROWNUM := 0)R WHERE
ENT = #{ent}
ENT = 0
AND (TITLE like concat('%',#{s_keyword},'%')) AND (CONTENT like concat('%',#{s_keyword},'%')) AND (CONTENT like concat('%',#{s_keyword},'%')) OR ( TITLE like contcat('%',#{s_keyword},'%'))
ORDER BY REF DESC, STEP ASC) A WHERE rnn between #{start} AND #{end}
